import React from 'react';
import Card from './Card.js';
import './CSS-Playing-Cards/cards.css';
import './card.css'

class Player extends React.Component {
   constructor(props){
      super(props);
      this.state = {
         money: 10000,
         bet: 0,
         numberOfCards: null,
         canPlay: false,
      };
      this.bidMoney = this.bidMoney.bind(this);
   }  
   joinTable() {
      if(this.state.money >= this.props.minBet) {
         let newMoney = this.state.money - this.props.minBet;
         this.setState({
            canPlay: true,
            money: newMoney,
         })
      }
   }

   bidMoney() {
      if(this.state.money-50 >= 0){
         let newBet = this.state.bet + 50;
         let newBudget = this.state.money - 50
         this.setState({
            money: newBudget,
            bet: newBet,
         });
      }
   }


    render() {       
      const cardsInHand = this.props.player.map((card, index) => <Card key={index} card={card}/>)
      return (
         <div>
            <ul className='hand_position'>
               {cardsInHand}
            </ul>
            <div className='button__container'>
               <button>Change Cards</button>
               <p>Current Money: {this.state.money}</p>
               <p>Current Bet: {this.state.bet}</p>
               <button onClick={this.bidMoney}>Bid</button>
            </div>
         </div>
      )
    }
};


export default Player;
