import React from 'react';
import Card from './Card.js';
import './CSS-Playing-Cards/cards.css';

class Player extends React.Component {
    componentDidMount(){
        const cardsInHand = this.props.player1Hand
        .map((card, index) => <Card key ={index} card={card}/>);
        console.log(cardsInHand);

    }
    render(){
        console.log(Array.isArray(this.props.player1Hand));
        console.log(this.props.player1Hand);
        return (
            <ul>
                {cardsInHand}
            </ul>
        );
    }
}

export default Player;
