import React from 'react';
import Player from '../player';
import {PokerHandRate, manageCardGame, drawCards, rateCards} from '../../poker/poker';
import './styles/cards.css';
import './styles/game.css';

class Game extends React.Component {  
    constructor(props){
        super(props);
        this.state = {
          game: {
            unshuffledDeck: null,
            deck: null,
            winner: null,
            pot: 0,
            minBet: 100,
          },
          player1: {
            player1Hand: [],
          },
          player2: {
            player2Hand: [],
          }
        }
        this.handleChangeP1 = this.handleChangeP1.bind(this);
        this.handleChangeP2 = this.handleChangeP2.bind(this);
        this.displayResults = this.displayResults.bind(this);
    }
    handleChangeP1 = (newHand) => {
      this.setState({
        player1: {
          player1Hand: newHand
        }
      })
    }
    handleChangeP2 = (newHand) => {
      this.setState({
        player2: {
          player2Hand: newHand
        }
      })
    }
    StartGame(){
      const unshuffledDeck = manageCardGame.createDeck();
      const deck = manageCardGame.shuffleDeck(unshuffledDeck);
      const hand1 = drawCards(5, deck);
      const hand2 = drawCards(5, deck);
      this.setState({
        game:{
          unshuffledDeck: unshuffledDeck,
          deck: deck,
          minBet: 100,
          winner: null,
          pot: 0,
        },
        player1: {
          player1Hand: hand1,
        },
        player2: {
          player2Hand: hand2,
        }
      })
    };

    displayResults() {
      PokerHandRate(new rateCards(this.state.player1.player1Hand));
    }
    componentDidMount(){
      this.StartGame();
      }

      render(){
        return(
          <div>
            <Player 
            deck={this.state.game.deck} 
            player={this.state.player1.player1Hand} 
            minBet={this.state.game.minBet}
            handleChange={this.handleChangeP1}
            displayResults={this.displayResults}
            />           
            <Player 
            deck={this.state.game.deck} 
            player={this.state.player2.player2Hand} 
            minBet={this.state.game.minBet}
            handleChange={this.handleChangeP2}
            displayResults={this.displayResults}
            />           
          </div>
        );
      }
}

export default Game;
