import React from 'react';
import Card from './Card.js';
import './CSS-Playing-Cards/cards.css';
import './card.css'

class Player extends React.Component {
   constructor(props){
      super(props);
      this.state = {
         money: 10000,
         bet: null,
         numberOfCards: null 
      };
   }
    render() {       
      const cardsInHand = this.props.player.map((card, index) => <Card key={index} card={card}/>)
      return (
         <div>
            <ul className='hand_position'>
               {cardsInHand}
            </ul>
            <div className='button__container'>
               <button>Change Cards</button>
               <button>Bid</button>
            </div>
         </div>
      )
    }
};


export default Player;
