      groupHandBySuit(hand).length === 1;
    }
    isHighCard(hand) {
      groupHandByWeight(hand).length === 5;
    }
    isStraightFlash(hand) {
      isFlush(hand) && isFiveInRow(hand);
    }
    isRoyalFlash(hand) {
      isFlush(hand) && isFiveInRow(hand) && hasAce(hand);
    }
