      const fGrouper = typeof grouper === 'function' ? grouper : item => item[grouper];
      const fList = list instanceof Array ? list : Object.values(list);

      return fList.reduce(
        (groups, item) => {
          const group = fGrouper(item);
          if (!groups[group]) {
              groups[group] = []
          }
          groups[group].push(item)
          return groups;
        },
        {}
      )
    }
