          while (m) {
            remainElem = Math.floor(Math.random() * m--);
            currentElem = deck[m];
            deck[m] = deck[remainElem];
            deck[remainElem] = currentElem;
        } 
        return deck;
      } 
    displayResults() {
      setTimeout(()=> {
        console.log(this.rateHand(this.state.player1Hand));
        console.log(this.rateHand(this.state.player2Hand));
      }, 5000);
    }  


       componentWillMount(){
