import React from 'react';
import Card from './Card.js';
import './CSS-Playing-Cards/cards.css';

class Player extends React.Component {

    cardsInHand(props){
        this.props.player1Hand.map((card, index) => <Card key={index} card={card}/>)
    }

    render(){
        const displayCards = this.cardsInHand()
        return (
            <ul>
                {displayCards}
            </ul>
        );
    }
}

export default Player;
